let images=document.querySelectorAll("img[data-src]");function winPosition(){let e=window.scrollY+window.outerHeight;for(var t=0;t<images.length;t++)e>=images[t].offsetTop&&(images[t].src=images[t].dataset.src)}window.addEventListener("scroll",winPosition),window.addEventListener("load",winPosition);let openPopup=document.querySelectorAll("[data-popup]");for(let e=0;e<openPopup.length;e++)openPopup[e].addEventListener("click",function(e){e.preventDefault();let t=e.target.dataset.popup,o=document.getElementById(t).innerHTML;document.getElementById("popupContentBox").innerHTML=o,document.body.classList.add("popup-active")});let closePopup=document.getElementById("closePopup");closePopup.addEventListener("click",function(e){e.preventDefault(),document.body.classList.remove("popup-active")});